package day3;

public class SwitchDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int userInput=5;
		switch(userInput) {
		case 1: System.out.println("Current recharge details");
		break;
		
		case 2: System.out.println("new offers");
		break;
		
		case 3: System.out.println("current recharge expiey");
		break;
		
		case 4: System.out.println("support");
		break;
		
		default:
			System.out.println("User input invalid");
		
		}

	}

}
